import React from 'react';

function Play() {
    const urlParams = new URLSearchParams(window.location.search);
    const gameID = urlParams.get('id');
    return (
        <div>
            {/* Contenido de la página "Play" */}
            <div style={{ padding: '16px' }}>
                {/* Aquí puedes agregar cualquier contenido adicional que desees */}
                <h1>Welcome to the Game</h1>

                {/* iframe para cargar la aplicación del motor de juegos */}
                <iframe
                    title="Game Engine"
                    src={`/engine/?id=${gameID}`}
                    style={{ width: '100%', height: 'calc(100vh - 64px)', border: 'none' }} // Resta 64px para dejar espacio para la barra superior
                ></iframe>
            </div>
        </div>
    );
};

export default Play;
