// Editor.js
import React, { useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppContext } from '../context';

function Editor() {
  const { gameID, token, API_KEY, DISCOVERY_DOCS, setSavedGame } = useAppContext();
  const navigate = useNavigate();
  const iframeRef = useRef(null);
  const [editorReady, setEditorReady] = useState(false);

  const handleEditorReady = useCallback((event) => {
      if (event.data && event.data.type === "editorReady") {
          setEditorReady(true);
      }
  }, []);

  const handleOpenEditor = useCallback(() => {
      if (editorReady) {
          const messageData = {
              type: 'initializeEditor',
              data: { gameID, token, API_KEY, DISCOVERY_DOCS }
          };
          iframeRef.current.contentWindow.postMessage(messageData, '*');
          console.log("Mensaje enviado");
      }
  }, [editorReady, gameID, token, API_KEY, DISCOVERY_DOCS]);

  useEffect(() => {
      window.addEventListener('message', handleEditorReady);
      return () => { window.removeEventListener('message', handleEditorReady); };
  }, [handleEditorReady]);

  useEffect(() => {
      if (editorReady) handleOpenEditor();
  }, [editorReady, handleOpenEditor]);

  return (
      <div style={{ width: '100%', height: '100vh', overflow: 'hidden' }}>
          <iframe
              title="Editor"
              id="editorIframe"
              ref={iframeRef}
              src="/editor"
              style={{ width: '100%', height: '100%', border: 'none' }}
          ></iframe>
      </div>
  );
}

export default Editor;
