//Footer.js 
import React, { useState } from 'react';
import { Box, Container, Link, IconButton, Select, MenuItem, useTheme, Tooltip } from '@mui/material';
import YouTubeIcon from '@mui/icons-material/YouTube';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
// Simulando un ícono para Discord usando ForumIcon como ejemplo
import ForumIcon from '@mui/icons-material/Forum'; 

// Opcional: Define tu componente de Logo si tienes un SVG o imagen específica
// Importa tu logo aquí si es una imagen: import logoImage from 'path/to/your/logo.png';

const Footer = () => {
  const theme = useTheme();
  const [language, setLanguage] = useState('en');

  const handleChangeLanguage = (event) => {
    setLanguage(event.target.value);
  };

  return (
    <Box
      component="footer"
      sx={{
        ...theme.customStyles.footer,
        backgroundColor: theme.palette.primary.main,
        color: theme.palette.primary.contrastText,
        py: 2,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}
    >
      <Container maxWidth="lg" sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          {/* Opcional: Incluye tu logo aquí si lo deseas */}
          {/* <img src={logoImage} alt="Logo" style={{ width: '100px', marginRight: theme.spacing(2) }} /> */}
          <Link href="#" color="inherit" sx={{ marginRight: theme.spacing(2) }}>Términos y Condiciones</Link>
          <Link href="#" color="inherit">Política de Privacidad</Link>
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Tooltip title="YouTube">
            <IconButton color="inherit" component="a" href="https://youtube.com">
              <YouTubeIcon />
            </IconButton>
          </Tooltip>
          <Tooltip title="Discord">
            <IconButton color="inherit" component="a" href="https://discord.com">
              <ForumIcon /> {/* Simulación, reemplaza con el ícono adecuado */}
            </IconButton>
          </Tooltip>
          <Tooltip title="Facebook">
            <IconButton color="inherit" component="a" href="https://facebook.com">
              <FacebookIcon />
            </IconButton>
          </Tooltip>
          <Tooltip title="Instagram">
            <IconButton color="inherit" component="a" href="https://instagram.com">
              <InstagramIcon />
            </IconButton>
          </Tooltip>
          <Select
            value={language}
            onChange={handleChangeLanguage}
            displayEmpty
            variant="standard"
            sx={{ color: 'inherit', ml: 2, '.MuiSvgIcon-root': { color: 'inherit' }, '&:before': { borderColor: 'transparent' }, '&:after': { borderColor: 'transparent' } }}
          >
            <MenuItem value="en">EN</MenuItem>
            <MenuItem value="es">ES</MenuItem>
            {/* Añade más idiomas según sea necesario */}
          </Select>
        </Box>
        {/* Copyright y opcionalmente el logo */}
        <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
          <Typography variant="body2" color="inherit" align="center" sx={{ flexGrow: 1 }}>
            {'© '}
            {new Date().getFullYear()}
            {' Your Website'}
          </Typography>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
