// FeaturesSection.js
import React from 'react';
import { Box, Grid, Card, CardContent, CardMedia, Typography, useTheme } from '@mui/material';
import intuitiveImage from '../images/intuitive.png';
import flexibleImage from '../images/flexible.png';
import powerfullImage from '../images/porwerfull.png';

// Datos de ejemplo para las características. Reemplázalos según tus necesidades.
const featuresData = [
  {
    title: 'Intuitivo',
    description: 'Una interfaz fácil de usar que te permite crear juegos rápidamente sin complicaciones.',
    imageUrl: intuitiveImage, // Asegúrate de reemplazar esto con la ruta real de tu imagen
  },
  {
    title: 'Flexible',
    description: 'Soporta una amplia variedad de tipos de juegos, permitiéndote llevar tus ideas a la realidad.',
    imageUrl: flexibleImage,
  },
  {
    title: 'Potente',
    description: 'Capaz de manejar proyectos complejos y ofrecer resultados impresionantes.',
    imageUrl: powerfullImage,
  },

];

const FeaturesSection = () => {
  const theme = useTheme();

  return (
    <Box sx={{ flexGrow: 1, mt: 8, bgcolor: theme.palette.background.default }}>
      <Typography variant="h4" component="h2" textAlign="center" gutterBottom>
        Características Principales
      </Typography>
      <Grid container spacing={2} justifyContent="center">
        {featuresData.map((feature, index) => (
          <Grid item key={index} xs={12} sm={6} md={4}>
            <Card sx={{ maxWidth: 345, m: 'auto' }}>
              <CardMedia
                component="img"
                height="140"
                image={feature.imageUrl}
                alt={feature.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {feature.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {feature.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default FeaturesSection;
