// HeroSection.js
import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import BackgroundImage from '../../images/hero.gif';
import GameEngineLogo from '../../images/loop.png';

const HeroSection = () => {
  const styles = {
    heroContainer: {
      minHeight: '50vh', // Altura mínima para asegurar que el contenido no se desborde
      backgroundImage: `url(${BackgroundImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      padding: '24px', // Añadir relleno horizontal para que el contenido no se desborde en dispositivos más pequeños
    },
    logo: {
      width: '400px', // Mantener el ancho fijo del logo
      marginBottom: '40px',
    },
    title: {
      color: '#fff', // Color blanco para el título
      marginBottom: '20px',
    },
    subtitle: {
      color: '#fff', // Color blanco para el subtítulo
      marginBottom: '20px',
    },
    startButton: {
      marginTop: '24px',
    },
  };

  return (
    <Box sx={styles.heroContainer}>
      <img src={GameEngineLogo} alt="Game Engine Logo" style={styles.logo} />
      <Typography variant="h2" component="h1" sx={styles.title}>
        Build Your Own 2D Games Easily
      </Typography>
      <Typography variant="h5" component="p" sx={styles.subtitle}>
        The simplest game engine in the world, completely free and open source.
      </Typography>
      <Button variant="contained" color="secondary" href="#register" sx={styles.startButton}>
        Start Now
      </Button>
    </Box>
  );
};

export default HeroSection;
