// Loop.js
import React, { useEffect, useState } from 'react';
import { Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import { initGoogleAPI, login, logout } from './apis/googleAPI';
import NavBar from './components/NavBar';
import Home from './pages/Home';
import Games from './pages/Games';
import Editor from './pages/Editor';
import Play from './pages/Play';
import SessionDialog from './components/SessionDialog';
import { useAppContext } from './context';
import { folderExists, createFolder } from './apis/driveAPI';

function Loop() {
  const { isDialogOpen, setIsDialogOpen, setGamesLoaded, setAppFolderID, userInfo, expirationTime, setExpirationTime, setToken, setUserInfo } = useAppContext();
  const navigate = useNavigate();
  const location = useLocation();
  const [lastLocation, setLastLocation] = useState(null);

  useEffect(() => {
    // Verifica la expiración de la sesión cada cierto intervalo
    const checkSessionExpiration = () => {
      const currentTime = new Date().getTime();
      const timeUntilExpiration = expirationTime - currentTime;
      const fiveMinutesInMilliseconds = 59.5 * 60 * 1000; // 5 minutos en milisegundos

      // Verifica si el usuario ha iniciado sesión antes de mostrar el diálogo
      if (userInfo && timeUntilExpiration <= fiveMinutesInMilliseconds && timeUntilExpiration > 0) {
        setIsDialogOpen(true);
        // Almacena la última ubicación antes de mostrar el diálogo
        setLastLocation(window.location.href); // Utiliza window.location.href para obtener la URL completa
      }
    };

    // Verifica la expiración de la sesión al cargar la aplicación
    checkSessionExpiration();

    // Verifica la expiración de la sesión cada cierto intervalo
    const checkInterval = setInterval(checkSessionExpiration, 1000); // Verifica cada segundo

    // Limpia el intervalo cuando el componente se desmonta
    return () => {
      clearInterval(checkInterval);
    };
  }, [expirationTime, setIsDialogOpen, userInfo]);

  const handleLogout = async () => {
    const token = await logout();
    setToken(token);
    setUserInfo(null);
    setExpirationTime(null); // Limpia el expirationTime al cerrar sesión
    setIsDialogOpen(false);
    navigate('/');
  };

  const handleLogin = async () => {
    setIsDialogOpen(false);
    setExpirationTime(-1);
    await initGoogleAPI();
    const { token, userInfo } = await login();
    // Calculate the exact moment in the future when the token will expire, in milliseconds
    const newExpirationTime = new Date().getTime() + token.expires_in * 1000;
    // Verificar y crear el directorio de juegos
    var appFolderID = await folderExists("Loop Games", token.access_token);
    if (!appFolderID) {
      appFolderID = await createFolder("Loop Games", 'root', token.access_token);
      setAppFolderID(appFolderID);
    } else {
      setAppFolderID(appFolderID);
    }
    setGamesLoaded(false); // Establece gameList en un array vacío
    setToken(token);
    setExpirationTime(newExpirationTime); // Actualiza el expirationTime
    setUserInfo(userInfo);

    // Navega a la última ubicación almacenada o a la página de inicio si no hay una ubicación almacenada
    navigate(lastLocation || '/games');
  };

  // Verifica si estás en la página del editor para no mostrar la barra global
  const isEditorPage = location.pathname.includes('/editor');

  return (
    <div style={{ height: '0px' }}>
      {!isEditorPage && (
        // Renderiza la barra global en todas las páginas excepto en la del editor
        <NavBar userInfo={userInfo} expirationTime={expirationTime} handleLogin={handleLogin} handleLogout={handleLogout} />
      )}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/games" element={<Games />} />
        <Route path="/editor" element={<Editor />} />
        <Route path="/play" element={<Play />} />
      </Routes>
      <SessionDialog
        isOpen={isDialogOpen}
        onLogin={handleLogin}
        onLogout={handleLogout}
      />
    </div>
  );
}

export default Loop;
