// UserMenu.js
import React from 'react';
import { Menu, MenuItem, Avatar, Typography, Divider } from '@mui/material';

const UserMenu = ({ userName, userPicture, onMyGamesClick, onLogoutClick, anchorEl, onClose }) => {
  return (
    <Menu
      anchorEl={anchorEl}
      open={Boolean(anchorEl)}
      onClose={onClose}
    >
      <div style={{ padding: '16px', textAlign: 'center' }}>
        <Avatar alt={userName} src={userPicture} />
        <Typography variant="body1" sx={{ marginTop: 1 }}>{userName}</Typography>
      </div>
      <Divider />
      <MenuItem onClick={onMyGamesClick}>My Games</MenuItem>
      <MenuItem onClick={onLogoutClick}>Logout</MenuItem>
    </Menu>
  );
};

export default UserMenu;
