// App.js

import React, { useState, useEffect } from 'react';
import './App.css';
import { initGoogleAPI, signIn, signOut, checkDriveFolder, listDriveGames } from './api';
import Header from './components/Header';
import SignInButton from './components/SignInButton';
import SignOutButton from './components/SignOutButton';
import NewGameButton from './components/NewGameButton';
import GameList from './components/GameList';

function App() {
  const [appFolderID, setAppFolderID] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    initGoogleAPI().then(() => {
      setAppFolderID(/* valor obtenido de la inicializaci贸n */);
      setUser(/* valor obtenido de la inicializaci贸n */);
    });
  }, []);

  return (
    <div>
      <Header user={user} />
      <SignInButton onClick={signIn} />
      <SignOutButton onClick={() => signOut(setAppFolderID)} />
      <h1>My Games</h1>
      <NewGameButton onClick={() => listDriveGames(appFolderID)} />
      <GameList appFolderID={appFolderID} />
    </div>
  );
}

export default App;
