// HeroSection.js
import React from 'react';
import { Box, Typography, Button, useTheme } from '@mui/material';
import BackgroundImage from '../../images/hero.gif';
import GameEngineLogo from '../../images/loop.png';

const HeroSection = () => {
  const theme = useTheme();
  const { heroSection } = theme; // Accede a tus estilos customizados

  return (
    <Box sx={{ ...heroSection.heroContainer, backgroundImage: `url(${BackgroundImage})` }}>
      <Box sx={heroSection.overlay} />
      <Box sx={heroSection.content}>
        <img src={GameEngineLogo} alt="Game Engine Logo" style={heroSection.logo} />
        <Typography variant="h2" component="h1" sx={{ ...heroSection.title, color: theme.palette.secondary.main }}>
          Build Your Own 2D Games Easily
        </Typography>
        <Typography variant="h5" component="p" sx={{ ...heroSection.subtitle, color: theme.palette.primary.main }}>
          The simplest game engine in the world, completely free and open source.
        </Typography>
        <Button variant="contained" color="secondary" href="#register" sx={heroSection.startButton}>
          Start Now
        </Button>
      </Box>
    </Box>
  );
};
