// Play.js
import React, { useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { useAppContext } from '../AppContext';

function Play() {
    const { gameID, token, API_KEY, DISCOVERY_DOCS } = useAppContext();
    const navigate = useNavigate();
    const iframeRef = useRef(null);

    const handleCloseEngine = useCallback(() => {
        navigate('/games');
    }, [navigate]);

    const handleOpenEngine = useCallback(() => {
        const messageData = {
            type: 'playGame',
            data: { gameID, token, API_KEY, DISCOVERY_DOCS }
        };
        iframeRef.current.contentWindow.postMessage(messageData, '*');
    }, [gameID, token, API_KEY, DISCOVERY_DOCS]);

    useEffect(() => {
        window.addEventListener('message', (event) => {
            if (event.data && event.data.type === "closeGame") {
                handleCloseEngine();
            }
        });

        return () => {
            window.removeEventListener('message', handleCloseEngine);
        };
    }, [handleCloseEngine]);

    return (
        <div style={{ 
          display: 'flex', 
          flexDirection: 'column', 
          height: 'calc(100vh - 264px)', // Asegúrate de ajustar la altura si es necesario
          position: 'relative', // Esto es clave para que el z-index del botón funcione correctamente
          overflow: 'hidden'
        }}>
            {/* Asegúrate de que el IconButton tenga un position definido si es necesario */}
            <IconButton
                style={{ 
                  position: 'absolute', // Posiciona absolutamente respecto al contenedor relativo
                  top: '8px', // Ajusta según sea necesario
                  right: '8px', // Ajusta según sea necesario
                  zIndex: 1000, // Un valor alto para asegurar que esté sobre otros elementos
                  color: 'white'
                }}
                onClick={handleCloseEngine}
            >
                <CloseIcon />
            </IconButton>
            <div style={{ flex: 1, overflow: 'auto' }}>
                <iframe
                    title="Game Engine"
                    ref={iframeRef}
                    onLoad={handleOpenEngine}
                    src={`/engine`}
                    style={{ width: '100%', height: '100%', border: 'none' }}
                ></iframe>
            </div>
        </div>
    );
}

export default Play;
