import './App.css';
import { useEffect, useState } from 'react';
import { jwtDecode } from 'jwt-decode';

const CLIENT_ID = '129246923501-4lk4rkmhin21kcaoul91k300s9ar9n1t.apps.googleusercontent.com';
const SCOPES = 'https://www.googleapis.com/auth/drive';

function App() {
  const [user, setUser] = useState({});
  const [tokenClient, setTokenClient] = useState({});

  function handleCallbackResponse(response) {
    console.log(response.credential);
    var userObject = jwtDecode(response.credential);
    console.log(userObject);
    setUser(userObject);
    document.getElementById('signInDiv').hidden = true;
  }

  function handleSignOut(event) {
    setUser({});
    document.getElementById('signInDiv').hidden = false;
  }

  function createDriveFile() {
    // Using gapi to create a file
    const fileMetadata = {
      name: 'loop test',
      mimeType: 'text/plain',
    };

    const token = tokenClient.getAuthResponse().access_token;

    gapi.client.drive.files.create({
      resource: fileMetadata,
      media: {
        mimeType: 'text/plain',
        body: 'Hello, World!',
      },
      fields: 'id',
    }).then((file) => {
      console.log('File ID:', file.result.id);
    }).catch((error) => {
      console.error('Error creating file:', error);
    });
  }

  useEffect(() => {
    /* global gapi */
    const gapi = window.gapi;

    // Load the client library and authenticate the user
    gapi.load('client:auth2', () => {
      gapi.client.init({
        clientId: CLIENT_ID,
        scope: SCOPES,
      }).then(() => {
        // Initialize Google Sign-In
        gapi.auth2.getAuthInstance().isSignedIn.listen(handleCallbackResponse);

        // Render Google Sign-In button
        gapi.auth2.getAuthInstance().render('signInDiv', { theme: 'outline', size: 'large' });

        // Initialize OAuth 2.0 token client
        setTokenClient(gapi.auth2.getAuthInstance().currentUser.get().getAuthResponse());
      });
    });
  }, []);

  return (
    <div className="App">
      <div id="signInDiv"></div>
      {Object.keys(user).length !== 0 && (
        <button onClick={(e) => handleSignOut(e)}>Sign Out</button>
      )}
      {user && (
        <div>
          <img src={user.picture} alt="user"></img>
          <h3>{user.given_name}</h3>
          <input type="submit" onClick={createDriveFile} value="Create File" />
        </div>
      )}
    </div>
  );
}

export default App;
