// App.js
import React, { useState, useEffect } from 'react';
import { initGoogleAPI, gisInit, signOut, signIn } from './api';

function App() {
  const [signInButtonEnabled, setSignInButtonEnabled] = useState(false);
  const [signOutButtonEnabled, setSignOutButtonEnabled] = useState(false);
  const [newGameButtonVisible, setNewGameButtonVisible] = useState(false);

  useEffect(() => {
    // Inicializamos las APIs y habilitamos el botón de Sign In
    initGoogleAPI(setSignInButtonEnabled)
      .then(() => gisInit(setSignInButtonEnabled, setSignOutButtonEnabled));
  }, []);

  const handleSignOut = () => {
    signOut(setSignInButtonEnabled, setSignOutButtonEnabled, setNewGameButtonVisible);
  };

  const handleSignIn = () => {
    signIn(setSignInButtonEnabled, setSignOutButtonEnabled, setNewGameButtonVisible);
  };

  return (
    <div className="App">
      {/* Resto de tu interfaz de usuario */}
      <button onClick={handleSignIn} disabled={!signInButtonEnabled}>
        Sign In
      </button>
      <button onClick={handleSignOut} disabled={!signOutButtonEnabled}>
        Sign Out
      </button>
      {/* Otros elementos de la interfaz */}
    </div>
  );
}

export default App;
