import React, { useState, useEffect } from 'react';
import { Navigate, Router, Route } from 'react-router-dom';
import { initGoogleAPI, signOut, signIn } from './googleAPI';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import Home from './pages/Home';
import Games from './pages/Games';

function App() {
  const [appFolderID, setAppFolderID] = useState(null);
  const [user, setUser] = useState(null);
  const [signInButtonVisible, setSignInButtonVisible] = useState(true);
  const [signOutButtonVisible, setSignOutButtonVisible] = useState(false);

  useEffect(() => {
    initGoogleAPI().then(() => {
      setUser(/* valor obtenido de la inicialización */);
      setSignInButtonVisible(true);
      setSignOutButtonVisible(false);
    });
  }, []);

  const handleSignOutClick = () => {
    signOut(() => {
      setSignInButtonVisible(true);
      setSignOutButtonVisible(false);
    });
  };

  const handleSignInClick = async () => {
    await initGoogleAPI();
    
    signIn(() => {
      // Lógica adicional después de obtener un token válido
      // Puedes hacer algo aquí antes de navegar a la página de juegos
      // ...

      // Navegar a la página de juegos después de obtener un token válido
      // Cambia "/games" a la ruta correcta para la página de juegos
      return <Navigate to="/games" />;
    });
  };

  return (
    <Router>
      <div>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              My App
            </Typography>
            {signInButtonVisible && <Button color="inherit" onClick={handleSignInClick}>Login</Button>}
            {signOutButtonVisible && <Button color="inherit" onClick={handleSignOutClick}>Logout</Button>}
          </Toolbar>
        </AppBar>

        <Routes>
          <Route path="/" element={<Home user={user} />} />
          <Route path="/games" element={<Games appFolderID={appFolderID} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
