import React, { useState, useEffect } from 'react';
import { folderExists, createFolder, listDriveGames, newGame, duplicateGame, deleteGame } from '../driveAPI';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import GameCard from '../components/GameCard';

const Games = ({ token }) => {
  const [appFolderID, setAppFolderID] = useState(null);
  const [gameList, setGameList] = useState([]);
  const [refreshGames, setRefreshGames] = useState(false);

  useEffect(() => {
    const initDrive = async () => {
      const folderID =  (await folderExists("Loop Games")) || (await createFolder("Loop Games", 'root', token.access_token));
      setAppFolderID(folderID);
    };
    initDrive();
  }, [token.access_token, appFolderID]);

  useEffect(() => {
    const fetchGames = async () => {
      if (appFolderID) {
        const games = await listDriveGames(appFolderID);
        setGameList(games);
      }
    };

    fetchGames();
  }, [appFolderID, refreshGames]);

  const handleNewGame = async () => {
    try {
      await newGame(appFolderID, token.access_token);
      setRefreshGames(prevState => !prevState);
    } catch (error) {
      console.error('Error creating new game:', error.message);
    }
  };

  const handleDuplicateGame = async (gameID) => {
    try {
      await duplicateGame(gameID);
      setRefreshGames(prevState => !prevState);
    } catch (error) {
      console.error('Error duplicating game:', error.message);
    }
  };

  const handleDeleteGame = async (gameID, gameName) => {
    try {
      await deleteGame(gameID, gameName);
      setRefreshGames(prevState => !prevState);
    } catch (error) {
      console.error('Error deleting game:', error.message);
    }
  };

  return (
    <div style={{ paddingLeft: '64px', paddingTop: '64px' }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '48px' }}>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={handleNewGame}
        >
          New Game
        </Button>
      </div>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
        {gameList.map((game) => (
          <GameCard key={game.id} game={game}
            handleDuplicateGame={handleDuplicateGame}
            handleDeleteGame={handleDeleteGame}
          />
        ))}
      </div>
    </div>
  );
};

export default Games;
