// Games.js
import React, { useEffect, useState, useCallback } from 'react';
import { listDriveGames, newGame, duplicateGame, deleteGame } from '../apis/driveAPI';
import Button from '@mui/material/Button';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import AddIcon from '@mui/icons-material/Add';
import GameCard from '../components/GameCard';
import { useAppContext } from '../AppContext'; // Ajusta según el nombre correcto
import { useNavigate } from 'react-router-dom';

const Games = () => {
  const { appFolderID, setGameID, gameList, setGameList } = useAppContext();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const fetchGames = useCallback(async () => {
    setLoading(true);
    try {
      const updatedGameList = await listDriveGames(appFolderID);
      setGameList(updatedGameList);
    } catch (error) {
      console.error('Error fetching games:', error);
    } finally {
      setLoading(false);
    }
  }, [appFolderID, setGameList]);

  useEffect(() => {
    fetchGames();
  }, [fetchGames]);

  async function handleAction(action, gameId = null) {
    setLoading(true);
    try {
      await action(gameId || appFolderID);
      fetchGames(); // Refetch games to reflect the changes
    } catch (error) {
      console.error('Error performing game operation:', error);
    } finally {
      setLoading(false);
    }
  }

  function handleNavigate(gameId, path) {
    setGameID(gameId);
    navigate(`/${path}`);
  }

  return (
    <div style={{ position: 'relative' }}>
      {loading && (
        <>
          <div
            style={{ position: 'fixed', width: '100%', height: 'calc(100% - 64px)', backgroundColor: `rgba(255,255,255, 0.5)`, zIndex: 9998 }}
          ></div>
          <Box
            style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: 9999 }}
          >
            <CircularProgress size={80} />
          </Box>
        </>
      )}
      {!loading && (
        <div style={{ paddingLeft: '64px', paddingTop: '64px' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '48px' }}>
            <Button
              variant="contained"
              color="primary"
              startIcon={<AddIcon />}
              onClick={() => handleAction(newGame)}
              disabled={loading}
            >
              New Game
            </Button>
          </div>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
            {gameList.map((game) => (
              <GameCard key={game.id} game={game}
                handleEditGame={() => handleNavigate(game.id, 'edit')}
                handlePlayGame={() => handleNavigate(game.id, 'play')}
                handleDuplicateGame={() => handleAction(duplicateGame, game.id)}
                handleDeleteGame={() => handleAction(deleteGame, game.id)}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Games;
