// Play.js
import React, { useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { useAppContext } from '../context';

function Play() {
    const { gameID, token, API_KEY, DISCOVERY_DOCS } = useAppContext();
    const navigate = useNavigate();
    const iframeRef = useRef(null);

    const handleCloseEngine = useCallback(() => {
        navigate('/games');
    }, [navigate]);

    const handleOpenEngine = useCallback(() => {
        if (!iframeRef.current) return;
        const messageData = {
            type: 'playGame',
            data: { gameID, token, API_KEY, DISCOVERY_DOCS }
        };
        const targetOrigin = window.location.origin;
        iframeRef.current.contentWindow.postMessage(messageData, targetOrigin);
    }, [gameID, token, API_KEY, DISCOVERY_DOCS]);

    const handleMessage = useCallback((event) => {
        if (event.origin !== window.location.origin) {
            return;
        }
        if (event.data && event.data.type === "closeGame") {
            handleCloseEngine();
        }
    }, [handleCloseEngine]);

    useEffect(() => {
        window.addEventListener('message', handleMessage);
        return () => {
            window.removeEventListener('message', handleMessage);
        };
    }, [handleMessage]);

    return (
        <div style={{ position: 'relative', width: '100%', height: '100vh', overflow: 'hidden' }}>
            <IconButton
                style={{ position: 'absolute', top: '8px', right: '8px', zIndex: 1, color: 'white' }}
                onClick={handleCloseEngine}
            >
                <CloseIcon />
            </IconButton>
            <iframe
                title="Game Engine"
                ref={iframeRef}
                onLoad={handleOpenEngine}
                src={`/engine`}
                style={{ width: '100%', height: '`calc(100% - 64px)', border: 'none' }}
            ></iframe>
        </div>
    );
}
export default Play;
