// Editor.js
import React, { useEffect } from 'react';

function Editor() {
  // Obtén el valor del parámetro "id" de la URL actual
  const urlParams = new URLSearchParams(window.location.search);
  const gameID = urlParams.get('id');
  useEffect(() => {
    const handleSavedGame = (event) => {
      if (event.origin !== window.location.origin) return;

      if (event.data.type === 'game_saved') {
        const savedGameData = event.data.data;
        // Aquí puedes manejar los datos, por ejemplo, actualizar el estado o el contexto
        console.log("Juego guardado recibido:", savedGameData);
      }
    };

    window.addEventListener('message', handleSavedGame);
    return () => {
      window.removeEventListener('message', handleSavedGame);
    };
  }, []); // Dependencias vacías para que se ejecute solo una vez al montar

  return (
    <div style={{ width: '100%', height: '100vh', overflow: 'hidden' }}>
      {/* Carga la ruta del editor con el valor de "id" como parte de la URL */}
      <iframe
        title="Editor"
        src={`/editor?id=${gameID}`} // Incluye el valor de "id" en la URL
        style={{ width: '100%', height: '100%', border: 'none' }}
      ></iframe>
    </div>
  );
}

export default Editor;
