// App.js
import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { initGoogleAPI, login, logout } from './googleAPI';
import NavBar from './components/NavBar';
import Wellcome from './pages/Home';
import Games from './pages/Games';
import Editor from './pages/Editor';
import { AppProvider } from './AppContext';
import { useAppContext } from './tu-contexto'; // Ajusta la ruta según tu estructura de carpetas
import { useNavigate } from 'react-router-dom';

function App() {
  const { token, setToken, userInfo, setUserInfo } = useAppContext();
  const navigate = useNavigate();

  const handleLogout = async () => {
    const token = await logout();
    setToken(token);
    setUserInfo(null);
    setGameID(null); // Restablecer el gameID al cerrar sesión
  };

  const handleLogin = async () => {
    await initGoogleAPI();
    const { token, userInfo } = await login();
    setToken(token);
    setUserInfo(userInfo);
    setGameID(null); // Restablecer el gameID al iniciar sesión
    navigate('/games');
  };

  return (
    <AppProvider>
      <div>
        <NavBar handleLogin={handleLogin} handleLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Wellcome />} />
          <Route path="/games" element={<Games />} />
          <Route path="/editor" element={<Editor />} />
        </Routes>
      </div>
    </AppProvider>
  );
}

export default App;
