import React, { useState, useEffect } from 'react';
import { Router, Routes, Route, useNavigate } from 'react-router-dom';
import { initGoogleAPI, signOut, signIn } from './googleAPI';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import Home from './pages/Home';
import Games from './pages/Games';

function App() {
  const [token, setToken] = useState(null);
  const [signInButtonVisible, setSignInButtonVisible] = useState(true);
  const [signOutButtonVisible, setSignOutButtonVisible] = useState(false);
  const navigate = useNavigate(); // Agrega esta línea

  useEffect(() => {
    console.log(token);
    if (token.access_token) {
      setSignInButtonVisible(false);
      setSignOutButtonVisible(true);
    } else {
      setSignInButtonVisible(true);
      setSignOutButtonVisible(false);
    }
  }, [token]);

  const handleSignOutClick = async () => {
    setToken(await signOut());
  };

  const handleSignInClick = async () => {
    await initGoogleAPI();
    setToken(await signIn());
    navigate('/games');
  };

  return (
    <Router>
      <div>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              My App
            </Typography>
            {signInButtonVisible && <Button color="inherit" onClick={handleSignInClick}>Login</Button>}
            {signOutButtonVisible && <Button color="inherit" onClick={handleSignOutClick}>Logout</Button>}
          </Toolbar>
        </AppBar>

        <Routes>
          <Route path="/" element={<Home token={token} />} />
          <Route path="/games" element={<Games token={token} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
