// src/pages/Games.js
import React, { useState, useEffect } from 'react';
import { setDriveAccessToken, checkDriveFolder, listDriveGames, newGame } from '../driveAPI';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import GameCard from '../components/GameCard'; // Ajusta la ruta según tu estructura de carpetas

const Games = ({ token }) => {
  const [appFolderID, setAppFolderID] = useState([]);
  const [games, setGames] = useState([]);

  useEffect(() => {
    const initDrive = async () => {
      try {
        await setDriveAccessToken(token.access_token);
        const id = await checkDriveFolder("Loop Games");
        setAppFolderID(id);
        const gamesList = await listDriveGames(id);
        setGames(gamesList);
      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    initDrive();
  }, [token.access_token]);

  const handleNewGameClick = async () => {
    try {
      const createdGameID = await newGame(appFolderID);
      const updatedGamesList = await listDriveGames(appFolderID);
      setGames(updatedGamesList);
      console.log("New game created with ID:", createdGameID);
    } catch (error) {
      console.error('Error creating new game:', error.message);
    }
  };

  const handleDeleteGame = async (gameID) => {
    try {
      await deleteGame(gameID);
      const updatedGamesList = await listDriveGames(appFolderID);
      setGames(updatedGamesList);
      console.log("Game deleted with ID:", gameID);
    } catch (error) {
      console.error('Error deleting game:', error.message);
    }
  };

  return (
    <div style={{ paddingLeft: '64px', paddingTop: '64px' }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '48px' }}>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={handleNewGameClick}
        >
          New Game
        </Button>
      </div>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
        {games.map((game) => (
           <GameCard key={game.id} game={game} handleDeleteGame={handleDeleteGame} />
        ))}
      </div>
    </div>
  );
};

export default Games;
