import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import { initGoogleAPI, login, logout } from './googleAPI';
import NavBar from './components/NavBar';
import Home from './pages/Home';
import Games from './pages/Games';

function App() {
  const [token, setToken] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    //console.log(userInfo, token)
  }, [userInfo,token]);

  const handleLogoutClick = async () => {
    const token = await logout();
    setToken(token);
    setUserInfo(null);
    navigate('/home');
  };

  const handleLoginClick = async () => {
      await initGoogleAPI();
      const { token, userInfo } = await login();
      setToken(token);
      setUserInfo(userInfo);
  };

  return (
    <div>
      <NavBar
        userInfo={userInfo}
        onSignOutClick={handleLogoutClick}
        onSignInClick={handleLoginClick}
        navigate={navigate}
      />
      <Routes>
        <Route path="/home" element={<Home token={token} />} />
        <Route path="/games" element={<Games token={token} />} />
      </Routes>
    </div>
  );
}

export default App;
