import React, { useState, useEffect } from 'react';
import './App.css';
import { initGoogleAPI, signOut, signIn } from './googleAPI';
import Header from './components/Header';
import SignInButton from './components/SignInButton';
import SignOutButton from './components/SignOutButton';
import NewGameButton from './components/NewGameButton';
import GameList from './components/GameList';

function App() {
  const [appFolderID, setAppFolderID] = useState(null);
  const [user, setUser] = useState(null);
  const [signInButtonEnabled, setSignInButtonEnabled] = useState(false);

  useEffect(() => {
    initGoogleAPI().then(() => {
      // setAppFolderID(/* valor obtenido de la inicialización */);
      // setUser(/* valor obtenido de la inicialización */);
      setSignInButtonEnabled(true); // Habilita el botón de sign-in después de la inicialización
    });
  }, []);

  const handleSignInClick = () => {
    signIn(() => {
      // Tu lógica después de iniciar sesión
    });
  };

  return (
    <div>
      <Header user={user} />
      {signInButtonEnabled && <SignInButton onClick={handleSignInClick} />}
      <SignOutButton onClick={() => signOut(setAppFolderID)} />
      <h1>My Games</h1>
      <GameList appFolderID={appFolderID} />
    </div>
  );
}

export default App;
