// theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#2E64EC',
    },
    secondary: {
      main: '#FFC107',
    },
  },
  components: {
    // Estilos globales
    MuiHeroSection: { // Nota: Este es un nombre de ejemplo, ajusta según tus necesidades
      styleOverrides: {
        root: {
          position: 'relative',
          color: '#fff',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          textAlign: 'center',
          // Añade aquí más estilos según necesites
        }
      }
    },
    MuiButton: {
      styleOverrides: {
        root: {
          // Estilos personalizados para todos los botones
        },
      },
    },
  },
  // Puedes definir tus propios valores personalizados aquí
  custom: {
    heroSection: {
      height: '100vh',
      color: '#fff',
      backgroundSize: 'cover',
      justifyContent: 'center',
    },
    heroText: {
      marginBottom: '1rem',
      fontWeight: 700,
    },
  },
});

export default theme;
