import React, { useState, useEffect } from 'react';
import './App.css';
import { initGoogleAPI, signOut, signIn } from './googleAPI';
import Header from './components/Header';
import SignInButton from './components/SignInButton';
import SignOutButton from './components/SignOutButton';

function App() {
  const [appFolderID, setAppFolderID] = useState(null);
  const [user, setUser] = useState(null);
  const [signInButtonVisible, setSignInButtonVisible] = useState(true);
  const [signOutButtonVisible, setSignOutButtonVisible] = useState(false);

  useEffect(() => {
    initGoogleAPI().then(() => {
     // setAppFolderID(/* valor obtenido de la inicialización */);
     // setUser(/* valor obtenido de la inicialización */);
      setSignInButtonVisible(true); // Oculta el botón de sign-in después de la inicialización
      setSignOutButtonVisible(false); // Muestra el botón de sign-out después de la inicialización
    });
  }, []);

  const handleSignOutClick = () => {
    signOut(() => {
      // Tu lógica después de cerrar sesión
      setSignInButtonVisible(true); // Muestra el botón de sign-in después de cerrar sesión
      setSignOutButtonVisible(false); // Oculta el botón de sign-out después de cerrar sesión
    });
  };

  return (
    <div>
      <Header user={user} />
      {signInButtonVisible && <SignInButton onClick={signIn} />}
      {signOutButtonVisible && <SignOutButton onClick={handleSignOutClick} />}
    </div>
  );
}

export default App;
