// Games.js
import React, { useEffect, useState } from 'react';
import { newGame, duplicateGame, deleteGame, listDriveGames } from '../apis/driveAPI';
import Button from '@mui/material/Button';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import AddIcon from '@mui/icons-material/Add';
import GameCard from '../components/GameCard';
import { useAppContext } from '../context';
import { useNavigate } from 'react-router-dom';

const Games = () => {
  const { token, setGameID, gameList, setGameList, appFolderID } = useAppContext();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (gameList.length === 0) {
      setLoading(true);
      listDriveGames(appFolderID, token.access_token)
        .then(updatedGameList => {
          setGameList(updatedGameList);
        })
        .catch(error => {
          console.error('Error fetching games:', error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [appFolderID, token.access_token, gameList.length, setGameList]);

  const handleNewGame = async () => {
    setLoading(true);
    try {
      const newGameResult = await newGame(appFolderID, token.access_token);
      setGameList(prevList => [...prevList, newGameResult]);
    } catch (error) {
      console.error('Error creating new game:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEditGame = (gameId) => {
    setGameID(gameId);
    navigate(`/edit/${gameId}`);
  };

  const handlePlayGame = (gameId) => {
    setGameID(gameId);
    navigate(`/play/${gameId}`);
  };

  const handleDuplicateGame = async (gameId) => {
    setLoading(true);
    try {
      await duplicateGame(gameId, token.access_token);
    } catch (error) {
      console.error('Error duplicating game:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteGame = async (gameId) => {
    setLoading(true);
    try {
      await deleteGame(gameId, token.access_token);
    } catch (error) {
      console.error('Error deleting game:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ position: 'relative' }}>
      {loading && (
        <Box sx={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 2000 }}>
          <CircularProgress />
        </Box>
      )}
      <Box sx={{ padding: '64px' }}>
        <Button variant="contained" startIcon={<AddIcon />} onClick={handleNewGame} disabled={loading}>
          New Game
        </Button>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: '16px', mt: 4 }}>
          {gameList.map((game) => (
            <GameCard key={game.id} game={game}
              onEdit={() => handleEditGame(game.id)}
              onPlay={() => handlePlayGame(game.id)}
              onDuplicate={() => handleDuplicateGame(game.id)}
              onDelete={() => handleDeleteGame(game.id)}
            />
          ))}
        </Box>
      </Box>
    </div>
  );
};

export default Games;
