import React, { useEffect, useRef } from 'react';
import { useAppContext } from '../AppContext';
import App from './editor/App'; // Ajusta la ruta según tu estructura de carpetas

const Editor = () => {
  const { token, gameID } = useAppContext();
  const editorContainerRef = useRef();

  useEffect(() => {
    const appInstance = new App(gameID, token);

    // Lógica para cargar el JSON y lanzar el editor
    // Puedes simular la llamada a los métodos onJsonLoaded, onImagesLoaded, onSoundsLoaded

    // Obtener el HTML del editor
    const editorHTML = appInstance.generateEditorHTML();

    // Limpiar el contenedor y agregar el HTML
    editorContainerRef.current.innerHTML = '';
    //editorContainerRef.current.appendChild(editorHTML);

  }, [token, gameID]);

  return <div ref={editorContainerRef}></div>;
};

export default Editor;
