// HeroSection.js
import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import BackgroundImage from '../../images/hero.gif';

const HeroSection = () => {
  return (
    <Box sx={{
      position: 'relative',
      backgroundImage: `url(${BackgroundImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      textAlign: 'center',
      '&::before': { content: '""', position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0, 0, 0, 0.5)', zIndex: 1 },
    }}>
      <Typography variant="h2" component="h1" gutterBottom>
        Build Your Own 2D Games Easily
      </Typography>
      <Typography variant="h5" component="p" gutterBottom>
        The simplest game engine in the world, completely free and open source.
      </Typography>
      <Button variant="contained" color="secondary" href="#register">
        Start Now
      </Button>
    </Box>
  );
};

export default HeroSection;
