// NavBar.js
import React from 'react';
import { AppBar, Toolbar, Typography, IconButton } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const NavBar = ({ userName, onSignOutClick, signInButtonVisible, signOutButtonVisible, onSignInClick }) => (
  <AppBar position="static">
    <Toolbar>
      <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
        Loop
      </Typography>
      {signInButtonVisible && (
        <IconButton color="inherit" onClick={onSignInClick} sx={{ fontSize: '0.8rem' }}>
          <AccountCircleIcon sx={{ marginRight: '0.5rem' }} />
          Iniciar sesi√≥n
        </IconButton>
      )}
      {signOutButtonVisible && (
        <>
          <Typography variant="h6" sx={{ marginRight: '10px' }}>
            {userName}
          </Typography>
          <IconButton color="inherit" onClick={onSignOutClick} sx={{ fontSize: '0.8rem' }}>
            <AccountCircleIcon sx={{ marginRight: '0.5rem' }} />
            Logout
          </IconButton>
        </>
      )}
    </Toolbar>
  </AppBar>
);

export default NavBar;
