// src/pages/Games.js
import React, { useState, useEffect } from 'react';
import { folderExists, createFolder, checkDriveFolder, listDriveGames, newGame, duplicateGame, deleteGame } from '../driveAPI';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import GameCard from '../components/GameCard'; // Ajusta la ruta según tu estructura de carpetas

const Games = ({ token }) => {
  const [appFolderID, setAppFolderID] = useState([]);
  const [gameList, setGameList] = useState([]);

  useEffect(() => {
    const initDrive = async () => {
      try {
        console.log(":::::::::::::::::::::::::::::::::::::::::::::::::");
        var folderID = await folderExists("Loop Games");
        if (!folderID)  folderID = createFolder("Loop Games", 'root', token.access_token);
    //    id = await checkDriveFolder("Loop Games");
        setAppFolderID(folderID);
        const gameList = await listDriveGames(folderID);
        setGameList(gameList);
      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    initDrive();
  }, [token.access_token]);

  const handleNewGame = async () => {
    try {
      const createdGameID = await newGame(appFolderID, token.access_token);
      const updatedgameList = await listDriveGames(appFolderID);
      setGameList(updatedgameList);
      console.log(updatedgameList)
      console.log("New game created with ID:", createdGameID);
    } catch (error) {
      console.error('Error creating new game:', error.message);
    }
  };

  const handleDuplicateGame = async (gameID) => {
    try {
      console.log("duplicate ", gameID);
      await duplicateGame(gameID);
      console.log("end duplicate");
      const updatedgameList = await listDriveGames(appFolderID);
      setGameList(updatedgameList);

    } catch (error) {
      console.error('Error duplicating game:', error.message);
    }
  };

  const handleDeleteGame = async (gameID, gameName) => {
    try {
      console.log("delete ", gameID);
      await deleteGame(gameID, gameName);
      const updatedgameList = await listDriveGames(appFolderID);
      setGameList(updatedgameList);
      console.log("Game deleted with ID:", gameID);
    } catch (error) {
      console.error('Error deleting game:', error.message);
    }
  };

  return (
    <div style={{ paddingLeft: '64px', paddingTop: '64px' }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '48px' }}>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={handleNewGame}
        >
          New Game
        </Button>
      </div>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
        {gameList.map((game) => (
          <GameCard key={game.id} game={game}
            handleDuplicateGame={handleDuplicateGame}
            handleDeleteGame={handleDeleteGame}
          />
        ))}
      </div>
    </div>
  );
};

export default Games;
