/* api.js */
/* global gapi, google  */
import { useState } from 'react';

const CLIENT_ID = '129246923501-4lk4rkmhin21kcaoul91k300s9ar9n1t.apps.googleusercontent.com';
const API_KEY = 'AIzaSyCfXON-94Onk-fLyihh8buKZcFIjynGRTc';
const DISCOVERY_DOCS = ["https://www.googleapis.com/discovery/v1/apis/drive/v3/rest"];
const SCOPES = 'https://www.googleapis.com/auth/drive ';

let tokenClient;
let gapiInited = false;
let gisInited = false;
let user;

export function initGoogleAPI() {
  console.log("Loading and initializing gapi...");
  return new Promise((resolve, reject) => {
    gapi.load('client', () => {
      gapi.client.init({
        apiKey: API_KEY,
        discoveryDocs: DISCOVERY_DOCS
      })
        .then(() => {
          gapiInited = true;
          return maybeEnableButtons();
        })
        .then(() => {
          console.log("gapi initialized successfully.");
          resolve(); // Resuelve la promesa cuando todo está listo
        })
        .catch((error) => {
          console.error("Error initializing gapi:", error);
          reject(error);
        });
    });
  });
}

export function gisInit() {
  return new Promise((resolve) => {
    console.log("Initializing gis...");
    tokenClient = google.accounts.oauth2.initTokenClient({
      client_id: CLIENT_ID,
      scope: SCOPES,
      prompt: ''
    });
    gisInited = true;
    maybeEnableButtons().then(() => {
      console.log("gis initialized successfully.");
      resolve(); // Resuelve la promesa después de la inicialización de gis
    });
  });
}

function maybeEnableButtons(setSignInButtonVisible) {
  return new Promise((resolve) => {
    if (gapiInited && gisInited) {
      setSignInButtonVisible(true);
    }
    console.log("Buttons enabled.");
    console.log("Everything initialized!");
    resolve(); // Resuelve la promesa después de la inicialización de los botones
  });
}

export function signOut(setSignInButtonVisible, setSignOutButtonVisible, setNewGameButtonVisible) {
  google.accounts.id.disableAutoSelect();
  // Aquí, podrías usar las funciones setState para actualizar el estado y controlar la visibilidad de los botones
  setSignInButtonVisible(true);
  setSignOutButtonVisible(false);
  setNewGameButtonVisible(false);
}

export function signIn(setSignInButtonVisible, setSignOutButtonVisible, setNewGameButtonVisible) {
  tokenClient.callback = (token) => {
    console.log(token);
    localStorage.setItem("token", JSON.stringify(token));
    const tokenExpiration = new Date().getTime() + (token.expires_in * 1000);
    localStorage.setItem('tokenExpiration', tokenExpiration);
    setSignInButtonVisible(false);
    setSignOutButtonVisible(true);
    setNewGameButtonVisible(true);
 
  };
  tokenClient.requestAccessToken({ prompt: '' });
}

function checkTokenExpiration() {
  const tokenExpiration = localStorage.getItem('tokenExpiration');
  var now = new Date().getTime();
  var timeRemaining = Math.floor((tokenExpiration - now) / 1000); // convert to seconds
  var minutes = Math.floor(timeRemaining / 60);
  var seconds = timeRemaining % 60;
  document.querySelector('.title').innerHTML = "Loop: " + user + " (" + minutes + " : " + seconds + ")";
  if (timeRemaining < 0) signIn();
}
