// HeroSection.js
import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import BackgroundImage from '../../images/hero.gif';
import GameEngineLogo from '../../images/loop.png';

const HeroSection = () => {
  const styles = {
    heroContainer: {
      position: 'relative',
      minHeight: '50vh',
      backgroundImage: `url(${BackgroundImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      padding: '36px',
    },
    overlay: {
      position: 'absolute', content: '""',
      top: 0, left: 0, right: 0, bottom: 0,
      backgroundColor: 'rgba(35, 35, 35, 0.5)', zIndex: 0,
    },
    content: {
      position: 'relative', zIndex: 1, textAlign: 'center',
    },
    logo: {
      width: '400px', marginBottom: '40px',
    },
    title: {
      color: '#fff', marginBottom: '20px',
    },
    subtitle: {
      color: '#fff', marginBottom: '20px',
    },
    startButton: {
      marginTop: '24px',
    },
  };

  return (
    <Box sx={styles.heroContainer}>
      <Box sx={styles.overlay} />
      <Box sx={styles.content}>
        <img src={GameEngineLogo} alt="Game Engine Logo" style={styles.logo} />
        <Typography variant="h2" component="h1" sx={styles.title}>
          Build Your Own 2D Games Easily
        </Typography>
        <Typography variant="h5" component="p" sx={styles.subtitle}>
          The simplest game engine in the world, completely free and open source.
        </Typography>
        <Button variant="contained" color="secondary" href="#register" sx={styles.startButton}>
          Start Now
        </Button>
      </Box>
    </Box>
  );
};

export default HeroSection;
