import React, { useEffect, useState } from 'react';
import gapi from 'gapi';

const CLIENT_ID = "TU_CLIENT_ID";
const API_KEY = "TU_API_KEY";
const SCOPES = "https://www.googleapis.com/auth/drive.file";

function App() {
  const [user, setUser] = useState({});
  const [files, setFiles] = useState([]);

  useEffect(() => {
    gapi.load('client:auth2', () => {
      gapi.client.init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/drive/v3/rest'],
        scope: SCOPES,
      }).then(() => {
        const authInstance = gapi.auth2.getAuthInstance();

        // Escucha cambios en el estado de autenticación.
        authInstance.isSignedIn.listen(updateSigninStatus);

        // Maneja el estado inicial de autenticación.
        updateSigninStatus(authInstance.isSignedIn.get());

        // Agrega un botón de inicio de sesión al DOM.
        authInstance.signInButton = authInstance.attachClickHandler('signInDiv', {}, handleAuthClick);
      });
    });
  }, []);

  const updateSigninStatus = (isSignedIn) => {
    if (isSignedIn) {
      const user = gapi.auth2.getAuthInstance().currentUser.get().getBasicProfile();
      setUser({
        id: user.getId(),
        name: user.getName(),
        email: user.getEmail(),
        picture: user.getImageUrl(),
      });

      listFiles();
    } else {
      setUser({});
      setFiles([]);
    }
  };

  const handleAuthClick = () => {
    gapi.auth2.getAuthInstance().signIn();
  };

  const listFiles = () => {
    gapi.client.drive.files.list({
      pageSize: 10,
      fields: 'nextPageToken, files(id, name)',
    }).then((response) => {
      const files = response.result.files;
      setFiles(files);
    }).catch((error) => {
      console.error('Error al listar archivos:', error);
    });
  };

  const handleSignOut = () => {
    gapi.auth2.getAuthInstance().signOut();
  };

  return (
    <div className="App">
      <div id="signInDiv"></div>
      {Object.keys(user).length !== 0 &&
        <div>
          <button onClick={handleSignOut}>Cerrar sesión</button>
          <img src={user.picture} alt={user.name} />
          <h3>{user.name}</h3>
        </div>
      }
      {files.length > 0 &&
        <div>
          <h2>Archivos en Google Drive:</h2>
          <ul>
            {files.map(file => (
              <li key={file.id}>{file.name}</li>
            ))}
          </ul>
        </div>
      }
    </div>
  );
}

export default App;
