import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { initGoogleAPI, signOut, signIn } from './googleAPI';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import Home from './pages/Home';
import Games from './pages/Games';

function App() {
  const [appFolderID, setAppFolderID] = useState(null);
  const [user, setUser] = useState(null);
  const [signInButtonVisible, setSignInButtonVisible] = useState(true);
  const [signOutButtonVisible, setSignOutButtonVisible] = useState(false);

  useEffect(() => {
    initGoogleAPI().then(() => {
      setUser(/* valor obtenido de la inicialización */);
      setSignInButtonVisible(true);
      setSignOutButtonVisible(false);
    });
  }, []);

  const handleSignOutClick = () => {
    signOut(() => {
      setSignInButtonVisible(true);
      setSignOutButtonVisible(false);
    });
  };

  return (
    <Router>
      <div>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              My App
            </Typography>
            {signInButtonVisible && <Button color="inherit" component={Link} to="/games">Login</Button>}
            {signOutButtonVisible && <Button color="inherit" onClick={handleSignOutClick}>Logout</Button>}
          </Toolbar>
        </AppBar>

        <Route path="/" exact component={() => <Home user={user} />} />
        <Route path="/games" component={() => <Games appFolderID={appFolderID} />} />
      </div>
    </Router>
  );
}

export default App;
