// loop.js
import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { initGoogleAPI, login, logout } from './apis/googleAPI';
import NavBar from './components/NavBar';
import Wellcome from './pages/Home';
import Games from './pages/Games';
import { useNavigate } from 'react-router-dom';
import { useAppContext } from './context';


function Loop() {
  const { userInfo, setToken, setUserInfo } = useAppContext();
  const navigate = useNavigate();

  const handleLogout = async () => {
    const token = await logout();
    setToken(token);
    setUserInfo(null);
  };

  const handleLogin = async () => {
    await initGoogleAPI();
    const { token, userInfo } = await login();
  
    // Calcula el momento exacto en el futuro cuando el token expirar치 en milisegundos
    const tiempoExpiracion = new Date().getTime() + token.expires_in * 1000;
    console.log(tiempoExpiracion);
  
    setToken({ ...token, tiempoExpiracion }); // Guardamos el tiempo de expiraci칩n
    setUserInfo(userInfo);
    navigate('/games');
  };
  


  return (
    <div>
      <NavBar userInfo={userInfo} handleLogin={handleLogin} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Wellcome />} />
        <Route path="/games" element={<Games />} />
      </Routes>
    </div>
  );
}

export default Loop;
