// Games.js
import React, { useEffect, useState } from 'react';
import { newGame, duplicateGame, deleteGame, listDriveGames } from '../apis/driveAPI';
import Button from '@mui/material/Button';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import AddIcon from '@mui/icons-material/Add';
import GameCard from '../components/GameCard';
import { useAppContext } from '../AppContext';
import { useNavigate } from 'react-router-dom';

const Games = () => {
  const {  setGameID, gameList, setGameList, appFolderID } = useAppContext();
  const [loading, setLoading] = useState(false);
  const [updateGameList, setUpdateGameList] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchGames();
  }, [appFolderID, updateGameList]);

  const fetchGames = async () => {
    setLoading(true);
    try {
      const updatedGameList = await listDriveGames(appFolderID);
      setGameList(updatedGameList);
    } catch (error) {
      console.error('Error fetching games:', error.message);
    } finally {
      setLoading(false);
    }
  };

  const performGameAction = async (action, gameId = null) => {
    setLoading(true);
    try {
      await action(gameId || appFolderID);
      setUpdateGameList(true); // Asumiendo que necesitas actualizar la UI tras la acción
      fetchGames(); // Refetch games to reflect the changes
    } catch (error) {
      console.error('Error performing game operation:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEditGame = (gameID) => {
    setGameID(gameID);
    navigate(`/edit`);
  };

  const handlePlayGame = (gameID) => {
    setGameID(gameID);
    navigate(`/play`);
  };

  return (
    <div style={{ position: 'relative' }}>
      {loading && (
        <>
          <div
            style={{ position: 'fixed', width: '100%', height: 'calc(100% - 64px)', backgroundColor: `rgba(255,255,255, 0.5)`, zIndex: 9998 }}
          ></div>
          <Box
            style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: 9999 }}
          >
            <CircularProgress size={80} />
          </Box>
        </>
      )}
      <div style={{ paddingLeft: '64px', paddingTop: '64px' }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '48px' }}>
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={() => performGameAction(newGame)}
            disabled={loading}
          >
            New Game
          </Button>
        </div>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
          {gameList.map((game) => (
            <GameCard key={game.id} game={game}
              handleEditGame={() => handleEditGame(game.id)}
              handlePlayGame={() => handlePlayGame(game.id)}
              handleDuplicateGame={() => performGameAction(duplicateGame, game.id)}
              handleDeleteGame={() => performGameAction(deleteGame, game.id, game.name)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Games;
