// Edit.js
import React, { useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppContext } from '../context';

function Edit() {
  const { gameID, setGameList, token, API_KEY, DISCOVERY_DOCS } = useAppContext();
  const navigate = useNavigate();
  const iframeRef = useRef(null);

  // Envuelve `updateGameInList` en un useCallback para evitar la recreación en cada render
  const updateGameInList = useCallback((updatedGame) => {
    setGameList((currentList) => {
      return currentList.map((game) =>
        game.id === updatedGame.id ? { ...game, ...updatedGame } : game
      );
    });
  }, [setGameList]); // Dependencias para useCallback

  const handleOpenEditor = useCallback(() => {
    if (!iframeRef.current) return;
    const messageData = {
      type: 'initEditor',
      data: { gameID, token, API_KEY, DISCOVERY_DOCS }
    };
    iframeRef.current.contentWindow.postMessage(messageData, window.location.origin);
  }, [gameID, token, API_KEY, DISCOVERY_DOCS]);

  const handleCloseEditor = useCallback((event) => {
    if (event.origin !== window.location.origin) return;
    if (event.data && event.data.type === "closeEditor") {
      updateGameInList(event.data.data);
      navigate('/games');
    }
  }, [navigate, updateGameInList]); 

  useEffect(() => {
    window.addEventListener('message', handleCloseEditor);
    return () => {
      window.removeEventListener('message', handleCloseEditor);
    };
  }, [handleCloseEditor]);

  return (
    <div style={{ width: '100%', height: '100vh', overflow: 'hidden' }}>
      <iframe
        title="Game Editor"
        ref={iframeRef}
        onLoad={handleOpenEditor}
        src="/editor"
        style={{ width: '100%', height: '100%', border: 'none' }}
      ></iframe>
    </div>
  );
}

export default Edit;
